CREATE PROCEDURE localization.add_localized_resource
(
	@resource_class		varchar(50),
    @culture_code		varchar(50),
    @key                varchar(50),
    @value              varchar(500)
)
AS
BEGIN
	DECLARE @resource_id    integer;
	SET @resource_id= -1

	IF(COALESCE(@culture_code, '') = '') BEGIN
    EXEC localization.add_resource @resource_class, @key, @value;
		RETURN;
    END 

	SELECT @resource_id = resource_id FROM localization.resources WHERE resource_class = @resource_class AND [key] = @key;


    IF EXISTS(SELECT 1 FROM localization.localized_resources WHERE culture_code=@culture_code AND resource_id=@resource_id) 
	BEGIN
		UPDATE localization.localized_resources
        SET value=@value
        WHERE localization.localized_resources.culture_code=@culture_code AND localization.localized_resources.resource_id = @resource_id;
		RETURN
	END

    INSERT INTO localization.localized_resources (resource_id,culture_code,[key],value) VALUES (@resource_id, @culture_code,@key, @value)
    
END
GO

