CREATE FUNCTION localization.get_localization_table
(
    @culture_code        varchar(50)
)
RETURNS TABLE

AS
RETURN  
(
    SELECT TOP(100) PERCENT
        row_number() OVER(ORDER BY t.[key]) AS id,
		t.resource_class,
        t.[key],
        t.invariant_resource original,
        t.value translated
    FROM 
	    (SELECT DISTINCT localization.resources.[key],
		localization.resources.resource_class,
        localization.resources.value as invariant_resource,
        localization.localized_resources.value
	    FROM localization.resources
		LEFT JOIN localization.localized_resources ON localization.resources.[key] = localization.localized_resources.[key]
		AND localization.localized_resources.culture_code = @culture_code) t
		ORDER BY t.[key] 
)
GO


