CREATE PROCEDURE office.add_office
(
    @office_code            varchar(12),
    @office_name            varchar(150),
    @nick_name              varchar(50),
    @registration_date      datetime,
    @currency_code          varchar(12),
    @currency_symbol        varchar(12),
    @currency_name          varchar(48),
    @hundredth_name         varchar(48),
    @fiscal_year_code       varchar(12),
    @fiscal_year_name       varchar(50),
    @starts_from            datetime,
    @ends_on                datetime,
    @sales_tax_is_vat       bit,
    @has_state_sales_tax    bit,
    @has_county_sales_tax   bit,
    @quotation_valid_days   integer,
    @income_tax_rate        decimal(24, 4),
    @week_start_day         integer,
    @transaction_start_date date,
    @is_perpetual           bit,
    @inv_valuation_method   varchar(5),
    @logo_file              varchar(500),
    @admin_name             varchar(100),
    @user_name              varchar(50),
    @password               varchar(48)
)
AS
BEGIN
    IF @starts_from > @ends_on 
	BEGIN
        RAISERROR('The start date cannot be greater than end date.',0,1)
    END

	DECLARE @inventory_system varchar(50)
	SET @inventory_system = '';
    IF(@is_perpetual = 0) BEGIN
        SET @inventory_system = 'Periodic'
    END

    IF NOT EXISTS(SELECT 0 FROM core.currencies WHERE currency_code=@currency_code) BEGIN
        INSERT INTO core.currencies(currency_code, currency_symbol, currency_name, hundredth_name) VALUES (@currency_code, @currency_symbol, @currency_name, @hundredth_name)
    END 

    UPDATE core.accounts
    SET currency_code = @currency_code;

	DECLARE @office_id int
    INSERT INTO office.offices(office_code, office_name, nick_name, registration_date, currency_code, income_tax_rate, transaction_start_date, week_start_day, primary_sales_tax_is_vat, has_state_sales_tax, has_county_sales_tax, logo_file)
    VALUES (@office_code, @office_name, @nick_name, @registration_date, @currency_code, @income_tax_rate, @transaction_start_date, @week_start_day, @sales_tax_is_vat, @has_state_sales_tax, @has_county_sales_tax, @logo_file)

    SELECT @office_id=@@IDENTITY

    --Inventory System
    IF EXISTS(SELECT * FROM office.configuration WHERE config_id = 1 AND office_id = @office_id) 
	BEGIN
        UPDATE office.configuration
        SET value = @inventory_system
        WHERE config_id = 1 AND office_id = @office_id;
	END
    ELSE
	BEGIN
        INSERT INTO office.configuration(config_id, office_id, value, configuration_details) VALUES(1, @office_id, @inventory_system, '')
    END

    IF(COALESCE(@quotation_valid_days, 0) = 0) BEGIN
        SET @quotation_valid_days = 15
    END 
    
    --Quotation valid duration
    IF EXISTS(SELECT * FROM office.configuration WHERE config_id = 3 AND office_id = @office_id) BEGIN
        UPDATE office.configuration
        SET value = CAST(@quotation_valid_days as varchar)
        WHERE config_id = 3 AND office_id = @office_id
	END
    ELSE
	BEGIN
        INSERT INTO office.configuration(config_id, office_id, value, configuration_details)
        VALUES (3, @office_id, CAST(@quotation_valid_days AS varchar), '')
    END 

    --COGS Calculation Method/Inventory Valuation Method
    IF EXISTS(SELECT * FROM office.configuration WHERE config_id = 2 AND office_id = @office_id) BEGIN
        UPDATE office.configuration
        SET value = @inv_valuation_method
        WHERE config_id = 2 AND office_id = @office_id
	END
    ELSE
	BEGIN
        INSERT INTO office.configuration(config_id, office_id, value, configuration_details)
        VALUES(2, @office_id, @inv_valuation_method, '')
    END
    
    IF NOT EXISTS(SELECT 0 FROM office.users WHERE user_name='sys') BEGIN
        INSERT INTO office.users(role_id, department_id, office_id, user_name, password, full_name) VALUES
        (office.get_role_id_by_role_code('SYST'), office.get_department_id_by_department_code('SUP'), @office_id, 'sys', '', 'System')
    END 
        
    INSERT INTO office.users(role_id, department_id, office_id,user_name,password, full_name, elevated)
    VALUES (office.get_role_id_by_role_code('ADMN'), office.get_department_id_by_department_code('SUP'), @office_id, @user_name, @password, @admin_name, 1)

	DECLARE @user_id integer
	SELECT @user_id=@@IDENTITY

    INSERT INTO policy.auto_verification_policy
    (
        office_id,
        user_id, 
        verify_sales_transactions, 
        sales_verification_limit,
        verify_purchase_transactions,
        purchase_verification_limit,
        verify_gl_transactions,
        gl_verification_limit,
        effective_from,
        ends_on,
        is_active
    )
    VALUES( 
        @office_id,
        @user_id,
        1,
        0,
        1,
        0,
        1,
        0,
        @starts_from,
        @ends_on,
        1)
        
    INSERT INTO core.fiscal_year(fiscal_year_code, fiscal_year_name, starts_from, ends_on, audit_user_id)
    VALUES(@fiscal_year_code, @fiscal_year_name, @starts_from, @ends_on, @user_id)

    INSERT INTO policy.menu_access(office_id, menu_id, [user_id])
    SELECT @office_id, core.menus.menu_id, @user_id
    FROM core.menus

END
GO

