CREATE FUNCTION office.is_admin(@user_id integer)
RETURNS bit
AS
BEGIN
    RETURN
    (
        SELECT office.roles.is_admin FROM office.users
        INNER JOIN office.roles
        ON office.users.role_id = office.roles.role_id
        WHERE office.users.user_id=@user_id
    )
END
